import React, {useState} from 'react'
import Searcho from './components/Searcho'
import axios from "axios";
import Listings from './components/Listings';


function App() {
    const api = 'http://www.omdbapi.com/?i=tt3896198&apikey=e3848925';
    const [state, setState] = useState({
        value : '',
        results: []
    });

    const keyEvent = (e) =>{
        const newValue = e.target.value
        // console.log(newValue)
        setState(prevState => {
            return {...prevState, value: newValue}
        })
    }

    const enterEvent = (e) => {
        // console.log(e.key)
        if(e.key === 'Enter'){
            axios(api + "&s=" + state.value).then(({data: {Search}}) => {
                // console.log(Search);
                setState(prevState => {
                    return {...prevState, results: Search}
                })
            });


        }
    }

    return (
        <div>
            <Searcho keyEvent={keyEvent} enterEvent={enterEvent} />
            <Listings show={state.results}/>
            {/* {state.results.map(state.result => {state.result.Title</div> })} */}
        </div>
    )
}

export default App
