import React, {useState} from 'react';
import Search from './components/Search';
import axios from 'axios'

function App() {
    // console.log({useState});
    const apiURL = 'http://www.omdbapi.com/?i=tt3896198&apikey=e3848925';
    const [state, setState] = useState({
        value:"",
        results : [],
        selected: {}

    });

    const search = (e) => {
        console.log(e)
        if(e.key === 'Enter'){
            axios(apiURL + "&s=" + state.value).then((data) => {
                console.log(data);
            })
        }
    }


    const typeEvent = (e) => {
        const value = e.target.value;
        // console.log(value);

        setState(prevState => {
            return {...prevState, value: value }
        })
    }

    return (
        <div className="App">
            <header>
            <h1>Movie Database </h1>
            </header>
            <main>
                <Search event={typeEvent} search={search}></Search>
                {/* <Result></Result> */}
            </main>
        </div>
    )
}

export default App
